import React, { Component } from 'react'
import { useState } from "react";
import{firestore,storage} from '../index';
import { Children } from 'react';
const AddNewCart = () => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [sell, setSell] = useState('');
    const [date, setDate] = useState('');
    const [image, setImage] = useState(null);

    const [imageError, setImageError] = useState('');

    const types = ['image/jpg', 'image/jpeg', 'image/png', 'image/PNG'];
    const handleProductImg = (e) => {
        let selectedFile = e.target.files[0];
        if (selectedFile) {
            if (selectedFile && types.includes(selectedFile.type)) {
                setImage(selectedFile);
                setImageError('');
            }
            else{
                setImage(null);
                setImageError('please select a valid image file type(png or jpg)')
            }
        }
        else {
            console.log('please select your file')
        }
    }
    const handleAddProducts=(e)=>{
        e.preventDefault();
       const uploadTask=storage.ref('product-images/${image.name}').put(image);
       uploadTask.on('state_changed',snapshot=>{
           const progress=(snapshot.bytesTransferred/snapshot.totalBytes)*100
           console.log()
       },error=>setUploadError(error.message),()=>{
           storage.ref('product-images'),Children(image.name).getDownloadURL().then(url=>{
            firestore.collection('Products').add({
                title,
                description,
                price:Number(price),
                sell:Number(sell),
                date:Date(date),
                url
            }).then(()=>{
                setSuccessMsg('product added sucesfuly');
                setTitle('');
                setDescription('');
                setPrice('');
                setSell('');
                setDate('');
                document.getElementById('file').value='';
                setImageError('');
                setUploadError('');
                setTimeout(()=>{
setSuccessMsg('');
                },3000)
            }).catch(error=>setUploadError(error.message));
           })
       })
    }
    const [successMsg, setSuccessMsg] = useState('');
    const [uploadError, setUploadError] = useState('');
    return (
        <div className='container'>
            <br></br>
            <br></br>
            <h1>Add Products</h1>
            <hr></hr>
            {successMsg && <>
                <div className='success-msg'>{successMsg}</div>
            </>}
            <br></br>
            <form autoComplete='off' className='form-group' onSubmit={handleAddProducts}>
                <label>Prodict Title</label>
                <input type='text' className='form-control' required
                    onChange={(e) => setTitle(e.target.value)} value={title}></input>
                <br></br>
                <label>Prodict Description</label>
                <input type='text' className='form-control' required
                    onChange={(e) => setDescription(e.target.value)} value={description}></input>
                <br></br>
                <label>Prodict Price</label>
                <input type='number' className='form-control' required
                    onChange={(e) => setPrice(e.target.value)} value={price}></input>
                <br></br>
                <label>Sell</label>
                <input type='number' className='form-control' required
                    onChange={(e) => setSell(e.target.value)} value={sell}></input>
                <br></br>
                <label>Date sell</label>
                <input type="date" className='form-control' required
                    onChange={(e) => setDate(e.target.value)} value={date}></input>
                <br></br>
                <label>Prodict Photo</label>
                <input type='file' id='file' className='form-control' required
                    onChange={handleProductImg} ></input>

                {imageError && <>
                    <br></br>
                    <div className='error-msg'>{imageError}</div>
                </>}
                <br></br>
                <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
                    <button type='submit' className='btn btn-success btn-md'>
                        SUBMIT
                    </button>
                </div>



            </form>
            {uploadError && <>
                <br></br>
                <div className='error-msg'>{uploadError}</div>
            </>}
        </div>
    );
};
export default AddNewCart;
