import React from 'react'

export const IndividualProduct = ({ individualProduct }) => {
    const Timer = (props:any) => {
        const {initialMinute = 0,initialSeconds = 0} = props;
        const [ minutes, setMinutes ] = useState(initialMinute);
        const [seconds, setSeconds ] =  useState(initialSeconds);
        useEffect(()=>{
        let myInterval = setInterval(() => {
                if (seconds > 0) {
                    setSeconds(seconds - 1);
                }
                if (seconds === 0) {
                    if (minutes === 0) {
                        clearInterval(myInterval)
                    } else {
                        setMinutes(minutes - 1);
                        setSeconds(59);
                    }
                } 
            }, 1000)
            return ()=> {
                clearInterval(myInterval);
              };
        });

        return (
            <div>
            { minutes === 0 && seconds === 0
                ? null
                : <h1> {minutes}:{seconds < 10 ?  `0${seconds}` : seconds}</h1> 
            }
            </div>
        )
    }


    return (

        <div className='product'>
            <div className='product-img'>
                <img src={individualProduct.url} alt="product-img" />
            </div>
            <div className='product-text title'>{individualProduct.title}</div>
            <div className='product-text description'>{individualProduct.description}</div>
            <div className='product-text price'>$ {individualProduct.price}</div>
            <div className='product-text price'>{individualProduct.date}</div>
            <div className='product-text rurultcost'>{individualProduct.price-(individualProduct.price/100*individualProduct.sell)}</div>
            <div className='product-text price'>{individualProduct.sell}</div>
            <div className='btn btn-danger btn-md cart-btn'  >ADD TO CART</div>
        </div>

    )
}
