import React from 'react'
import { firestore, storage } from '../index'
import { Products } from './Products'
import ChangeCart from './ChangeCart'
import { data } from './ChangeCart'
import firebase from 'firebase'
import { useState } from 'react'
import { InputGroup } from 'react-bootstrap'
import { FormControl } from 'react-bootstrap'
import { title } from 'process'
import Table from 'react-bootstrap/Table'

export const IndividualChangeProduct = ({ individualChangeProduct }) => {

    // const allInputs = {imgUrl: ''};


    const [image, setImage] = useState(null);
    const [url, setUrl] = useState("");
    const [progress, setProgress] = useState(0);

    const handleChange = e => {
        if (e.target.files[0]) {
            setImage(e.target.files[0]);
        }
    };

    const handleUpload = () => {
        const uploadTask = storage.ref(`product-images/${image.name}`).put(image);
        uploadTask.on(
            "state_changed",
            snapshot => {
                const progress = Math.round(
                    (snapshot.bytesTransferred / snapshot.totalBytes) * 100
                );
                setProgress(progress);
            },
            error => {
                console.log(error);
            },
            () => {
                storage
                    .ref("product-images")
                    .child(image.name)
                    .getDownloadURL()
                    .then(url => {
                        setUrl(url);
                    });
            }
        );
    };

    console.log("image: ", image);












    const funcUpdateImage = () => {
        // var myValue = document.getElementById("inputGroup-sizing-lg").value={title};
        //const inpu= document.getElementById("file").value;




        return (
            firestore.collection("Products").doc(individualChangeProduct.ID).update({
                "url": url
            })
                .then(() => {
                    // console.log("Document successfully updated!");
                }))
    }


    const funcUpdateTitle = () => {
        // var myValue = document.getElementById("inputGroup-sizing-lg").value={title};
        const input = document.getElementById("title").value;




        return (
            firestore.collection("Products").doc(individualChangeProduct.ID).update({
                "title": input
            })
                .then(() => {
                    // console.log("Document successfully updated!");
                }))
    }

    const funcUpdateDisc = () => {
        // var myValue = document.getElementById("inputGroup-sizing-lg").value={title};
        const description = document.getElementById("description").value;




        return (
            firestore.collection("Products").doc(individualChangeProduct.ID).update({
                "description": description
            })
                .then(() => {
                    // console.log("Document successfully updated!");
                }))
    }

    const funcUpdatePrice = () => {
        // var myValue = document.getElementById("inputGroup-sizing-lg").value={title};
        const price2 = document.getElementById("price").value;




        return (
            firestore.collection("Products").doc(individualChangeProduct.ID).update({
                "price": price2
            })
                .then(() => {
                     console.log(price2);
                }))
    }

    const funcUpdateData = () => {
        // var myValue = document.getElementById("inputGroup-sizing-lg").value={title};
        const data = document.getElementById('data').value;




        return (
            firestore.collection("Products").doc(individualChangeProduct.ID).update({
                "date": data
            })
                .then(() => {
                     console.log("Document successfully updated!");
                }))
    }
    const funcUpdateSell = () => {
        // var myValue = document.getElementById("inputGroup-sizing-lg").value={title};
        const sell = document.getElementById("sell").value;




        return (
            firestore.collection("Products").doc(individualChangeProduct.ID).update({
                "sell": sell
            })
                .then(() => {
                     console.log(individualChangeProduct.ID);
                }))
    }





    const deletePost = async (name) => {
        const snapshot = await firestore.collection('Products').limit(1).where('title', '==', name).get();

        const doc = snapshot.docs[0];
        doc.ref.delete();
        return doc.id;
    }








    return (



        <div className='product'>
        <Table striped bordered hover>
  <thead>
    <tr>
      <th>#</th>
      <th>First Name</th>
      <th>Last Name</th>
      <th>Username</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>1</td>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <td>2</td>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <td>3</td>
      <td colSpan="2">Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</Table>


        </div>

    )
}
