import React, { useState, useEffect } from 'react';

import { firestore, storage } from '../index';
import { Products } from './ProductsChange';
import { IndividualChangeProduct } from './IndividualChangeProducts';

import Table from 'react-bootstrap/Table'
const ChangeCart=()=>{











    const [products, setProducts] = useState([]);

    var styles_product_box =  {
        box: {display: 'flex','flex-wrap':'wrap'}
      };
    const getProducts = async () => {
        const products = await firestore.collection('Products').get();
        const productsArray = [];
        for (var snap of products.docs) {
            var data = snap.data();
            data.ID = snap.id;
            productsArray.push({
                ...data
            })
            if (productsArray.length === products.docs.length) {
                setProducts(productsArray);
            }
        }
    }
    useEffect(() => {
        getProducts();
    }, [])

    return(
        <>
        <br></br>
        {products.length > 0 && (
            <div className='container-fluid'>
                <h1 className='text-center'>Products</h1>
                <Table striped bordered hover>
  <thead>
    <tr>
      <th>#</th>
      <th>{indi}</th>
      <th>Last Name</th>
      <th>Username</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>1</td>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <td>2</td>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <td>3</td>
      <td colSpan="2">Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</Table>

                <div className='producs-box' style={styles_product_box.box}>

                    <Products products={products} />
                    </div>
                </div>
        )}
        {products.length< 1 &&(
            <div className='container-fluid'>
            Please wait........
            </div>
        )}
    </>
    );
};
export default ChangeCart;
