import { firestore, storage } from '../index'
import { Products } from './Products'
import ChangeCart from './ChangeCart'
export const IndividualChangeProduct = ({ individualChangeProduct }) => {
    const products =  firestore.collection('Products');
const name=individualChangeProduct.title;
const deleteUserByName = async (name) => {
    const snapshot = await firestore
      .collection("Products")
      .limit(1)
      .where("name", "==", name)
      .get();

    const doc = snapshot.docs[0];
    doc.ref.delete();

    console.log(doc.id);
    return doc.id;
  };
    return (

        <div className='product'>
            <div className='product-img'>
                <img src={individualChangeProduct.url} alt="product-img" />
            </div>

            <div className='product-text title'>{individualChangeProduct.title}</div>
            <div className='product-text description'>{individualChangeProduct.description}</div>
            <div className='product-text price'>$ {individualChangeProduct.price}</div>
            <div className='btn btn-danger btn-md cart-btn' onClick={() => deleteUserByName(individualChangeProduct.title)}>dfd</div>
        </div>

    )
}
