import React from 'react'

export const IndividualProduct = ({ individualProduct }) => {

    class Timer extends Component {
        constructor() {
          super();
          this.state = {
            hours: 0,
            minutes: 0,
            seconds:0
          }
          this.hoursInput = React.createRef();
          this.minutesInput= React.createRef();
          this.secondsInput = React.createRef();
        }

        inputHandler = (e) => {
          this.setState({[e.target.name]: e.target.value});
        }

        convertToSeconds = ( hours, minutes,seconds) => {
          return seconds + minutes * 60 + hours * 60 * 60;
        }

        startTimer = () => {
          this.timer = setInterval(this.countDown, 1000);
        }

        countDown = () => {
          const  { hours, minutes, seconds } = this.state;
          let c_seconds = this.convertToSeconds(hours, minutes, seconds);

          if(c_seconds) {

            // seconds change
            seconds ? this.setState({seconds: seconds-1}) : this.setState({seconds: 59});

            // minutes change
            if(c_seconds % 60 === 0 && minutes) {
              this.setState({minutes: minutes -1});
            }

            // when only hours entered
            if(!minutes && hours) {
              this.setState({minutes: 59});
            }

            // hours change
            if(c_seconds % 3600 === 0 && hours) {
              this.setState({hours: hours-1});
            }

          } else {
            clearInterval(this.timer);
          }
        }


        stopTimer = () => {
          clearInterval(this.timer);
        }

        resetTimer = () => {
          this.setState({
            hours: 0,
            minutes: 0,
            seconds: 0
          });
          this.hoursInput.current.value = 0;
          this.minutesInput.current.value = 0;
          this.secondsInput.current.value = 0;
        }


        render() {
          const { hours, minutes, seconds } = this.state;



    return (

        <div className='product'>
            <div className='product-img'>
                <img src={individualProduct.url} alt="product-img" />
            </div>
            <div className='product-text title'>{individualProduct.title}</div>
            <div className='product-text description'>{individualProduct.description}</div>
            <div className='product-text price'>$ {individualProduct.price}</div>
            <div className='product-text price'>{individualProduct.date}</div>
            <div className='product-text rurultcost'>{individualProduct.price-(individualProduct.price/100*individualProduct.sell)}</div>

            <div className="App">
               <h1 className="title"> (( React Countdown )) </h1>
               <div className="inputGroup">
                  <h3>Hrs</h3>
                  <input ref={this.hoursInput} type="number" placeholder={0}  name="hours"  onChange={this.inputHandler} />
                  <h3>Min</h3>
                  <input  ref={this.minutesInput} type="number"  placeholder={0}   name="minutes"  onChange={this.inputHandler} />
                  <h3>Sec</h3>
                  <input   ref={this.secondsInput} type="number"  placeholder={0}  name="seconds"  onChange={this.inputHandler} />
               </div>
               <div>
                  <button onClick={this.startTimer} className="start">start</button>
                  <button onClick={this.stopTimer}  className="stop">stop</button>
                  <button onClick={this.resetTimer}  className="reset">reset</button>
               </div>
               <h1> Timer {hours}: {minutes} : {seconds} </h1>
            </div>


            <div className='product-text price'>{individualProduct.sell}</div>
            <div className='btn btn-danger btn-md cart-btn'  >ADD TO CART</div>
        </div>

    )
}
}}
