import React, { useState, useEffect } from 'react';

import { firestore, storage } from '../index';
import { Products } from './ProductsChange';
import { IndividualChangeProduct } from './IndividualChangeProducts';
const ChangeCart=()=>{

    const [count, setCount] = useState(0) // используем его

    const UserTable = props => {
        const handleDeleteUser = id => {
          // не забываем спросить пользователя,
          // действительно ли он хочет удалить запись
          let answer = window.confirm('Are you sure?')

          if (answer) {
            props.deleteUser(id)
          }
        }
        return (
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Username</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {props.users.length > 0 ? (
                props.users.map(user => (
                  <tr key={user.id}>
                    <td>{user.name}</td>
                    <td>{user.username}</td>
                    <td>
                      <button className="button muted-button">Edit</button>
                      <button
                        className="button muted-button"
                        onClick={() => handleDeleteUser(user.id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={3}>No users</td>
                </tr>
              )}
            </tbody>
          </table>
        )
      }



    return (
      <div className="container">

        <h1>CRUD App with Hooks</h1>
        <div className="flex-row">
          <div className="flex-large">
            <h2>Add user</h2>
            {/* пример */}
            <p>you clicked {count}</p>
            <button onClick={() => setCount(count + 1)}>Click me</button>
          </div>
          <div className="flex-large">
            <h2>View users</h2>
          </div>
        </div>
        <UserTable />
      </div>

    )
  }

export default ChangeCart;
